import React from 'react'

export default function ArticleShow({match,articles}) {
    const article = articles.find(e => e.id === parseInt( match.params.articleId))
    const display_article = () => {
        console.log(article.name)

    }
    return (
        <div>
            <h1>MovieShow</h1>
            {display_article()}

        </div>
    )
}
