import React from 'react'
import { Link } from 'react-router-dom';
import ArticleShow from '../components/ArticleShow'
import {Route} from 'react-router-dom'


export default function Article({article}) {



    return (
        <div>
            <div>
                Name: {article.name}
                <br />
                Picture: <img src={article.picture} />
                <br />

                Price: $ {article.price} 
                <br />
                <br />

            </div>
            <button>Add To Cart</button> 

            <button><Link to={`articles/${article.id}`}>More Details</Link></button>
            < Route  path={`articles/:articleId`} render={renderProps => <ArticleShow articles={this.props.articles} {...renderProps}/>}/>

        </div>
    )
}
