import React, { Component } from 'react'
import fetchCart from '../actions/fetchCart'
import {connect} from 'react-redux'
import deleteFromCart from '../actions/deleteFromCart'
import Loader from '../components/Loader'

 class Cart extends Component {
    componentDidMount(){
        this.props.fetchCart()
    }

    display_articles = () => {

       return this.props.articles_in_cart.map(e => <div key={e.id}>
                                           Name: {e.name} <br />
                                           Pic: <img src={e.picture} />
                                           Price: {e.price} <br />
                                            <button className='positive ui button'>Pay</button> 
                                            <button className='negative ui button' onClick={() =>this.props.deleteFromCart(e.id)}>Remove</button>
                                            <form action="https://www.paypal.com/cgi-bin/webscr" method="post" target="_top">
<input type="hidden" name="cmd" value="_xclick"/>
<input type="hidden" name="business" value="cloe.kouadjo@my.tccd.edu"/>
<input type="hidden" name="lc" value="AR"/>
<input type="hidden" name="item_name" value="iphone x"/>
<input type="hidden" name="amount" value={e.price}/>
<input type="hidden" name="currency_code" value="USD"/>
<input type="hidden" name="button_subtype" value="services"/>
<input type="hidden" name="no_note" value="0"/>
<input type="hidden" name="tax_rate" value="8.000"/>
<input type="hidden" name="shipping" value="5.00"/>
<input type="hidden" name="bn" value="PP-BuyNowBF:btn_buynowCC_LG.gif:NonHostedGuest"/>
<input type="image" src="https://www.paypalobjects.com/en_US/i/btn/btn_buynowCC_LG.gif" border="0" name="submit" alt="PayPal - The safer, easier way to pay online!"/>
<img alt="" border="0" src="https://www.paypalobjects.com/en_US/i/scr/pixel.gif" width="1" height="1"/>
</form>

                                        </div>)
    }
    render() {
        return (
            <div>
                <h1>Cart</h1>

                <h3>Total: {this.props.total} </h3>

                {
                   !this.props.loading ? this.display_articles() : <Loader />
                }
            </div>
        )
    }
}

const mapStateToProps = state => {
    return {
        loading_cart: state.cart.loading,
        articles_in_cart: state.cart.articles,
        total: state.cart.total
    }
}

const mapDispatchToProps =(dispatch) => {
    return {fetchCart:  () => dispatch(fetchCart()), deleteFromCart: id => dispatch(deleteFromCart(id))}
}
export default connect(mapStateToProps, mapDispatchToProps)(Cart)
