import React, { Component } from 'react'
import fetchCart from '../actions/fetchCart'
import {connect} from 'react-redux'
import deleteFromCart from '../actions/deleteFromCart'
import Loader from '../components/Loader'

 class Cart extends Component {
    componentDidMount(){
        this.props.fetchCart()
    }

    display_articles = () => {

       return this.props.articles_in_cart.map(e => <div key={e.id}>
                                           Name: {e.name} <br />
                                           Pic: <img src={e.picture} />
                                           Price: {e.price} <br />
                                            <button>Pay</button> 
                                            <button onClick={() =>this.props.deleteFromCart(e.id)}>Delete</button>
                                        </div>)
    }
    render() {
        return (
            <div>
                <h1>Cart</h1>

                <h3>Total: {this.props.total} </h3>

                {
                   !this.props.loading ? this.display_articles() : <Loader />
                }
            </div>
        )
    }
}

const mapStateToProps = state => {
    return {
        loading_cart: state.cart.loading,
        articles_in_cart: state.cart.articles,
        total: state.cart.total
    }
}

const mapDispatchToProps =(dispatch) => {
    return {fetchCart:  () => dispatch(fetchCart()), deleteFromCart: id => dispatch(deleteFromCart(id))}
}
export default connect(mapStateToProps, mapDispatchToProps)(Cart)
