import React, { Component } from 'react'

export default class SearchBar extends Component {
    constructor(){
        super()
        this.state = {querry: ''}
    }
    handleChange = event => {
        this.setState({querry: event.target.value})
    }
    handleSubmit = event => {
        event.perventDefault()

    }
    render() {
        return (
            <form className="ui category search">
                {/* <div className="ui icon input"> */}
                    <input onChange={this.handleChange} className="prompt" type="text" placeholder="Enter an article" value={this.state.querry}/>
                    <i className="search icon"></i>
                {/* </div> */}
                <div className="results"></div>
            </form>
        )
    }
}
